mapper.select:
		SELECT s.sno, SUM(s.amount) AS amount, s.arrivedatetime, max(s.orderdatetime), s.uno, s.mno, m.buyprice, m.code, m.company_name, m.name, m.sellprice,
 m.type, m.img_path
FROM stock s INNER JOIN merchandise m ON s.mno = m.mno GROUP BY s.mno ORDER BY max(s.orderdatetime) DESC LIMIT 5;
